// Ambil output string
const output = $json.output;

// Split berdasarkan newline (\n)
const splitted = output.split('\n');

// Proses array: Trim, Filter kosong, Pisahkan Nomor dan Judul
const processed = splitted
  .map(item => item.trim())         // Hilangkan spasi
  .filter(item => item.length > 0)  // Hanya item tidak kosong
  .map(item => {
    // Pisahkan berdasarkan ". " (angka diikuti titik dan spasi)
    const match = item.match(/^(\d+)\.\s*(.*)$/);

    if (match) {
      return {
        json: {
          number: parseInt(match[1], 10), // Nomor (angka)
          title: match[2]                 // Judul
        }
      };
    } else {
      return {
        json: {
          number: null,
          title: item
        }
      };
    }
  });

return processed;

const raw = $json.output;
const maxWords = 15; // Aggressive for spoken/subtitle

const lines = raw
  .split('\n')
  .filter(line => /^\|\s*\d+\s*\|/.test(line)); 

let initialSegments = lines.map(line => {
  const cols = line.split('|').map(s => s.trim());
  return cols[2];
});

function aggressiveSplit(segment, maxWords) {
  let splits = [];
  // First, split at . ; : , (preserve punctuation)
  let regex = /(?<=[.,;:])\s+/g;
  let basePieces = segment.split(regex);
  for (let piece of basePieces) {
    piece = piece.trim();
    if (piece && piece.split(/\s+/).length > maxWords) {
      // Further split at 'and', 'but', etc.
      let subpieces = piece.split(/\s+(and|but|or|so|yet)\s+/i);
      for (let sub of subpieces) {
        sub = sub.trim();
        if (sub) splits.push(sub);
      }
    } else if (piece) {
      splits.push(piece);
    }
  }
  return splits;
}

// Utility for filtering
const skipWords = [
  "but", "and", "or", "so", "yet", "however", "ultimately", "though", "although",
  "if", "then", "also", "for example", "moreover", "however,", "ultimately,", "but,", "and,", "or,", "so,", "yet,"
];

// Decide if a segment is visual/meaningful enough for image prompting
function isGoodForImagePrompt(segment) {
  const s = segment.trim();
  const words = s.toLowerCase().split(/\s+/);
  if (words.length < 3) return false;
  // Filter if starts with a skip word
  if (skipWords.includes(words[0])) return false;
  // Optionally, you could add further checks for common non-visual fragments
  return true;
}

let newSegments = [];
let counter = 1;

for (const seg of initialSegments) {
  const text = seg.trim();
  if (text.split(/\s+/).length > maxWords) {
    const splits = aggressiveSplit(text, maxWords);
    for (const s of splits) {
      if (s && isGoodForImagePrompt(s)) {
        newSegments.push({ No: counter++, ScriptSegment: s });
      }
    }
  } else {
    if (isGoodForImagePrompt(text)) {
      newSegments.push({ No: counter++, ScriptSegment: text });
    }
  }
}

return newSegments.map(row => ({ json: row }));

const header = items[items.length - 1].json;

return items.slice(0, -1).map(item => {
  return {
    json: {
      No: item.json.No,
      TTMPrompt: item.json.TTMPrompt,
      SBTS: item.json.SBTS,
      ScriptSegment: item.json.ScriptSegment,
      Status: header.Status,
      spreadsheetId: header.spreadsheetId,
      sheetId: header.sheetId,
      Judul: header.Judul
    }
  };
});

return items.map(item => {
  const text = item.json.output || '';
const cleaned = text
  .replace(/^(.{0,120}script (for|about|on)[^:]{0,120}:?\s*)/i, '')
  .replace(/[“”]/g, '"')
  .replace(/[‘’]/g, "'")
  .replace(/[–—]/g, '-')
  .replace(/\u00a0/g, ' ')
  .replace(/\*/g, '')
  .replace(/[^\x00-\x7F]/g, '')
  .replace(/^"(.*)"$/, '$1');
return { json: { ...item.json, cleanedText: cleaned } };
});

return items.map(item => {
  const raw = item.json.output || ""; // Handle jika output tidak ada

  // Split at 'Speech Bubble Text Suggestion:'
  const [ttmPrompt, sbtsSection] = raw.split('Speech Bubble Text Suggestion:');

  // Clean up TTMPrompt (jika ada)
  const TTMPrompt = (ttmPrompt || "").trim();

  // Extract SBTS (Speech Bubble Text Suggestion)
  let SBTS = "";
  if (sbtsSection) {
    // Tangkap teks di dalam kutip (biasa atau smart quote)
    const match = sbtsSection.match(/["“](.*?)["”]/);
    if (match) {
      SBTS = match[1].trim();
    } else {
      // Jika tidak ada kutip, ambil semua setelah separator
      SBTS = sbtsSection.trim();
    }
  }

  return {
    json: {
      ...item.json,
      TTMPrompt,
      SBTS
    }
  };
});
