parameters.jsCode:
function getRandomItems(A, X) {
  return A
    .slice()
    .sort(() => Math.random() - 0.5)
    .slice(0, X);
}

const items = getRandomItems($input.all().map(item => item.json.data), 3);

return [{
  json: {
    items
  }
}]
